<template>
  <div class="select-wrap">
    <div class="select-input" @click="handleToggleOptions" :class="{'is-open': isOpen}">
      <div class="select-items">山东省</div>
      <input type="text" autocomplete="off">
    </div>
  </div>
</template>
<script>
export default {
  data() {
    return {
      isOpen: false,
    };
  },

  methods: {
    handleToggleOptions() {
      this.isOpen = !this.isOpen;
    },
  },
};
</script>
